mapper.select:
SELECT s_company.name as comp_name, r_owner.* FROM r_owner LEFT JOIN s_company on r_owner.comp_id = s_company.id
LEFT JOIN r_owner_property on r_owner_property.owner_id= r_owner.id
where r_owner.is_delete=0
and r_owner.comp_id = #{compId}
and r_owner_property.comm_id = #{commId}
and r_owner.name like CONCAT('%',#{name},'%')
LIMIT ${pageNo},${size}

SELECT s_company.NAME AS comp_name, r_owner.owner_type, r_owner.NAME, r_owner.owner_addr, r_owner.tel, r_owner.e_mail, r_owner.cert_type, r_owner.cert_number, r_owner.industry, r_owner.sex, r_owner.native_place, r_owner.education, r_owner.state, r_owner.link_name, r_owner.link_tel, r_owner.link_addr, r_owner.remark, r_owner.created_name, r_owner.created_at, r_owner.modified_name, r_owner.modified_at FROM r_owner LEFT JOIN s_company ON s_company.id = r_owner.comp_id
LEFT JOIN r_owner_property ON r_owner_property.owner_id = r_owner.id
WHERE r_owner.is_delete = 0
AND r_owner.comp_id = #{compId}
AND r_owner_property.comm_id = #{commId}

SELECT count (r_owner.id) FROM r_owner
LEFT JOIN r_owner_property ON r_owner_property.owner_id = r_owner.id
WHERE r_owner.is_delete = 0
AND r_owner.comp_id = #{compId}
AND r_owner_property.comm_id = #{commId}
and r_owner.name like CONCAT('%',#{name},'%')
