mapper.select:
SELECT s_company.NAME AS comp_name, r_community.NAME AS comm_name, r_comm_area.NAME AS comm_area_name, r_building.NAME AS building_name, r_owner.name as owner_name, r_owner.owner_type as owner_type, r_owner.owner_addr as owner_addr, r_owner.tel, r_owner.e_mail as e_mail, r_owner.cert_type as cert_type, r_owner.cert_number as cert_number, r_owner.industry as industry, r_owner.sex as sex, r_owner.native_place as native_place, r_owner.education as education, r_owner.link_name as link_name, r_owner.link_tel as link_tel, r_owner.link_addr as link_addr, r_owner_property.* FROM r_owner_property LEFT JOIN r_owner on r_owner_property.owner_id = r_owner.id LEFT JOIN s_company ON r_owner_property.comp_id = s_company.id LEFT JOIN r_community ON r_owner_property.comm_id = r_community.id LEFT JOIN r_comm_area ON r_owner_property.comm_area_id = r_comm_area.id LEFT JOIN r_building ON r_owner_property.building_id = r_building.id WHERE r_owner_property.is_delete = 0
and r_owner_property.comp_id = #{compId}
and r_owner_property.comm_id = #{commId}
and r_owner.owner_type = #{ownerType}
and r_owner.name like '%${ownerName}%'
and r_owner.cert_number = #{certNumber}
and r_owner_property.property_type = #{propertyType}
and r_owner_property.property_id = #{propertyId}
LIMIT #{pageNo},#{size}

SELECT
    count(r_owner_property.id)
FROM
    r_owner_property
LEFT JOIN r_owner on r_owner_property.owner_id = r_owner.id
WHERE
r_owner_property.is_delete = 0

    and r_owner_property.comp_id = #{compId}


    and  r_owner_property.comm_id = #{commId}


    and r_owner.owner_type = #{ownerType}


    and  r_owner.name like '%${ownerName}%'


    and  r_owner.cert_number = #{certNumber}


    and  r_owner_property.property_type = #{propertyType}


    and  r_owner_property.property_id = #{propertyId}
