mapper.select:
SELECT s_company.name as comp_name, r_community.name as comm_name, r_comm_area.* FROM r_comm_area LEFT JOIN r_community ON r_comm_area.comm_id = r_community.id
LEFT JOIN s_user_comm ON r_community.id = s_user_comm.comm_id
LEFT JOIN s_company on r_community.comp_id = s_company.id WHERE 1=1
and s_user_comm.user_id = #{userId}
and r_comm_area.comm_id = #{commId}
and r_comm_area.is_delete=0
LIMIT #{pageNo},#{size}

SELECT count (r_comm_area.id) FROM r_comm_area LEFT JOIN r_community ON r_comm_area.comm_id = r_community.id
LEFT JOIN s_user_comm ON r_community.id = s_user_comm.comm_id
LEFT JOIN s_company on r_community.comp_id = s_company.id WHERE 1=1
and s_user_comm.user_id = #{userId}
and r_comm_area.comm_id = #{commId}
and r_comm_area.is_delete=0
